string stringToIntString(string tresc){
    stringstream bufor;
    for(int i=0; i<tresc.size();i++){
        bufor<< setfill('0') << setw(3)<<oct<<int(tresc[i]);
    }
    return bufor.str();
}

string intStringToString(string tresc){
    string wynik;
    for(int i=0; i<tresc.size();i+=3){
        stringstream bufor;
        for(int k=i;k<i+3;k++) {
            bufor<<tresc[k];
        }
        wynik+=char(stoi(bufor.str(), nullptr,8));
    }
    return wynik;
}

string wczytajDane() {
    short wybor;
    string tresc;
    string tekst;
    cout << "Podaj metode wprowadzania danych:" << endl
         << "[1] Tekst wprowadzony z klawiatury" << endl
         << "[2] Tekst wczytywany z pliku" << endl
         << "Wybierz opcje: ";
    while(true) {
        cin >> wybor;
        cin.ignore();
        if((wybor == 1) || (wybor == 2)) {
            break;
        } else {
            cout << "Nieprawidlowa opcja. Wybierz opcje: ";
        }
    }
    if(wybor == 1) {
        cout << "Wprowadz tekst:";
        getline(cin, tekst);
        tresc = tekst;
    }
    else if(wybor == 2) {
        fstream plik;
        plik.open("../dane.txt", ios::in );
        if( plik.good() ) {
            getline(plik, tresc);
            plik.close();
        } else cout << "Blad - nie udalo sie otworzyc pliku dane.txt" << endl;
    }
    return stringToIntString(tresc);
}


